import React, { useState, useEffect } from 'react';
import styles from "./styles.module.css";
import videoBg from '../assests/home page video.mp4';
import carLogoVideo from '../assests/wallpaperjpg-MyVideo-Convert2video-com.mp4';

const Main = () => {
    const [loggedIn, setLoggedIn] = useState(!!localStorage.getItem("token"));

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    useEffect(() => {
        const handleScroll = () => {
            window.scrollTo({
                left: window.scrollY, // Set horizontal scroll position to vertical scroll position
                behavior: 'smooth', // Optional: Add smooth scrolling effect
            });
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className={styles.main_container}>
            {loggedIn ? (
                <div className={styles.video_section}>
                    <video src={videoBg} autoPlay loop className={styles.background_video} />
                    <div className={styles.content}>
                        <h1>Welcome</h1>
                        <p>To my site.</p>
                    </div>
                    <nav className={styles.navbar}>
                        <h1>fakebook</h1>
                        <button className={styles.white_btn} onClick={handleLogout}>
                            Logout
                        </button>
                    </nav>
                </div>
            ) : (
                <div className={styles.main_container}>
                    <nav className={styles.navbar}>
                        <h1>fakebook</h1>
                        <button className={styles.white_btn} onClick={handleLogout}>
                            Logout
                        </button>
                    </nav>
                </div>
            )}
            <div className={styles.car_logo_section}>
                <video src={carLogoVideo} autoPlay loop muted className={styles.car_logo_video} />
            </div>
        </div>
    );
};

export default Main;
