import React, { useState, useEffect } from 'react';
import styles from "./styles.module.css";
import videoBg from '../assests/home page video.mp4';
import PreLoader from '../Preloader/Preloader'; // Import the PreLoader component
import { preLoaderAnim } from '../'; // Import animation styles

const Main = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            // Simulating asynchronous login check
            setTimeout(() => {
                setLoggedIn(true);
                setLoading(false);
            }, 2000); // Simulating 2 seconds delay
        } else {
            setLoading(false);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    useEffect(() => {
        preLoaderAnim(); // Call preLoaderAnim function when Main component mounts
    }, []);

    return (
        <div>
            {loading ? (
                // Render the PreLoader component while loading is true
                <PreLoader />
            ) : (
                loggedIn ? (
                    <div>
                        <div className="overlay"></div>
                        <video src={videoBg} autoPlay loop />
                        <div className={styles.content}>
                            <h1>Welcome</h1>
                            <p>To my site.</p>
                        </div>
                        <nav className={styles.navbar}>
                            <h1>fakebook</h1>
                            <button className={styles.white_btn} onClick={handleLogout}>
                                Logout
                            </button>
                        </nav>
                    </div>
                ) : (
                    <div className={styles.main_container}>
                        <nav className={styles.navbar}>
                            <h1>fakebook</h1>
                            <button className={styles.white_btn} onClick={handleLogout}>
                                Logout
                            </button>
                        </nav>
                    </div>
                )
            )}
        </div>
    );
};

export default Main;
