import React, { useState, useEffect,ev } from 'react';
import styles from "./styles.module.css";
import videoBg from '../assests/home page video.mp4';
import carLogoVideo from '../assests/wallpaperjpg-MyVideo-Convert2video-com.mp4';

const Main = () => {
    const [loggedIn] = useState(!!localStorage.getItem("token"));

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    useEffect(() => {
        const handleScroll = (event) => {
            const { scrollLeft, scrollTop, clientWidth, scrollWidth } = event.target.documentElement;
            const maxScrollLeft = scrollWidth - clientWidth;
            const isScrollingHorizontally = Math.abs(event.deltaX) > Math.abs(event.deltaY);

            if (isScrollingHorizontally) {
                // Calculate the new horizontal scroll position
                const newScrollLeft = scrollLeft + event.deltaX;

                // Ensure the new scroll position stays within bounds
                const adjustedScrollLeft = Math.max(0, Math.min(newScrollLeft, maxScrollLeft));

                // Scroll horizontally
                event.target.documentElement.scrollLeft = adjustedScrollLeft;
            } else {
                // Scroll vertically
                window.scrollTo({
                    top: scrollTop + event.deltaY,
                    behavior: 'smooth',
                });
            }
        };

        window.addEventListener('wheel', handleScroll, { passive: false });

        return () => {
            window.removeEventListener('wheel', handleScroll);
        };
    }, []);


    return (
        <div className={styles.main_container}>
            {loggedIn ? (
                <div className={styles.video_section}>
                    <video src={videoBg} autoPlay loop className={styles.background_video} />
                    <div className={styles.content}>
                        <h1>Welcome</h1>
                        <p>To my site.</p>
                    </div>
                    <nav className={styles.navbar}>
                        <h1>CARMASTER DEPOT</h1>
                        <button className={styles.white_btn} onClick={handleLogout}>
                            Logout
                        </button>
                    </nav>
                </div>
            ) : (
                <div className={styles.main_container}>
                    <nav className={styles.navbar}>
                        <h1>fakebook</h1>
                        <button className={styles.white_btn} onClick={handleLogout}>
                            Logout
                        </button>
                    </nav>
                </div>
            )}
            <div className={styles.car_logo_section}>
                <video src={carLogoVideo} autoPlay loop muted className={styles.car_logo_video} />
            </div>
        </div>
    );
};

export default Main;
