import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import benzLogo from '../assests/BENZ-LOGO.jpg';
import benzVideo from '../assests/BENZVIDEO.mp4'; // Import the video file
import styles from './BenzCollectionPage.module.css'; // Import CSS file for styling

const BenzCollectionPage = () => {
    const [carDetails, setCarDetails] = useState({
        modelName: '',
        description: '',
        image: null
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setCarDetails({ ...carDetails, [name]: value });
    };

    const handleImageChange = (event) => {
        setCarDetails({ ...carDetails, image: event.target.files[0] });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Handle form submission, e.g., send data to server or update state
        console.log(carDetails);
    };

    return (
        <div className={styles.container}>
            <img src={benzLogo} alt="Benz Logo" className={styles.logo} />
            <div className={styles.text}>Explore Our Benz Collection</div>
            <div className={styles.videoContainer}>
                <video src={benzVideo} controls autoPlay loop className={styles.video} />
            </div>
            <div className={styles.formContainer}>
                <div className={styles.formHeader}>Please Fill Out the Details to Display Your Car</div>
                <form onSubmit={handleSubmit} className={styles.form}>
                    <input
                        type="text"
                        name="modelName"
                        value={carDetails.modelName}
                        onChange={handleInputChange}
                        placeholder="Model Name"
                    />
                    <textarea
                        name="description"
                        value={carDetails.description}
                        onChange={handleInputChange}
                        placeholder="Description"
                    />
                    <input type="file" onChange={handleImageChange} accept="image/*" />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>
    );
};

export default BenzCollectionPage;
